import React, { useRef, useCallback, useState } from "react";
import Modal from "../Layout/Modal";
import { motion } from "framer-motion";

const Players = (props) => {
  const codenameRef = useRef("");
  const nameRef = useRef("");

  const submitHandler = (event) => {
    event.preventDefault();

    const player = {
      name: nameRef.current.value.trim(),
    };
  };
  return (
    <React.Fragment>
      <motion.form
        onSubmit={submitHandler}
        className={`account__${
          props.inputType === "add" ? "add" : "remove"
        }Player`}
      >
        <input type="number" min="1" max="4" placeholder="tier" />
        <input
          type="text"
          placeholder="codename"
          ref={codenameRef}
          className={`${codenameValid ? "" : "notValid"}`}
        />
        <input
          type="text"
          placeholder="full name"
          ref={nameRef}
          className={`${codenameValid ? "" : "notValid"}`}
        />
        <button type="submit">{props.inputType === "add" ? "+" : "-"}</button>
      </motion.form>
    </React.Fragment>
  );
};

export default Players;
