import React, { useState } from "react";
import { motion } from "framer-motion";

const Modal = (props) => {
  const [closeModal, setCloseModal] = useState(false);


  return (
    <div>
      <motion.section
        className={props.isVisible ? "modal warning" : "modal warning hidden"}
        initial={{ x: "-27vw", y: "-25vh" }}
        animate={props.isVisible || closeModal ? {scale:1 , y:"-25vh"} : {scale:0, y: "-70vh"}}
      >
        <h3 className="modal-header">{props.modalH}</h3>
        <p className="modal-info">{props.modalP}</p>
        <button
          className="btn-understand btn"
          onClick={closeModal}
        >
          {props.modalBtn}
        </button>
      </motion.section>

      <section
        className={props.isVisible ? "overlay" : "overlay hidden"}
      ></section>
    </div>
  );
};

export default Modal;
