import React from "react";

const Players = (props) => {
  let playerInputClass;
  if (props.type === "add") {
    playerInputClass = "account__addPlayer";
  } else {
    playerInputClass = "account__removePlayer";
  }
  return (
    <React.Fragment>
      <form className={playerInputClass}>
        <input type="number" min="1" max="4" placeholder="tier" />
        <input type="text" placeholder="codename" />
        <input type="text" placeholder="full name" />
        <button type="submit">Add player to Account</button>
      </form>
    </React.Fragment>
  );
};

export default Players;
