import React, { useRef } from "react";
import Modal from "../Layout/Modal";

const Players = (props) => {
  const codenameRef = useRef("");

  const submitHandler = () => {
    if (codenameRef.current.value.length > 3) {
      console.warn("Player ");
    }
  };

  if (codenameRef.current.current.value)
    return (
      <React.Fragment>
        <form
          className={`account__${
            props.inputType === "add" ? "add" : "remove"
          }Player`}
        >
          <input type="number" min="1" max="4" placeholder="tier" />
          <input type="text" placeholder="codename" ref="codenameRef" />
          <input type="text" placeholder="full name" />
          <button type="submit" onSubmit={submitHandler}>
            {props.inputType === "add" ? "+" : "-"}
          </button>
        </form>
      </React.Fragment>
    );
};

export default Players;
