import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { NavLink } from "react-router-dom";
import { motion } from "framer-motion";

const Profile = (props) => {
  const dispatch = useDispatch();
  const isLoggedIn = useSelector((state) => state.isLoggedIn);
  const [enteredEmail, setEnteredEmail] = useState();
  const [enteredPassword, setEnteredPassword] = useState();

  const loginSubmitHandler = async (event) => {
    event.preventDefault();
    console.log(enteredEmail, enteredPassword);
    await fetch("https://apis.ssdevelopers.xyz/auth/login", {
      method: "POST",
      body: JSON.stringify({
        email: enteredEmail,
        pass: enteredPassword,
      }),
    });
    dispatch({ type: "LOGIN" });
  };

  const emailInputHandler = (event) => {
    setEnteredEmail(event.target.value);
  };
  const passwordInputHandler = (event) => {
    setEnteredPassword(event.target.value);
  };

  return (
    <React.Fragment>
      <div className="login">
        <motion.div
          className="login-con"
          animate={
            isLoggedIn
              ? { opacity: 0, x: "-60%", y: "-50%" }
              : { opacity: 1, x: "-50%", y: "-50%" }
          }
          transition={{ ease: "easeOut", duration: 0.3 }}
        >
          <div className="login-text">
            <h1>Log In to SS Account</h1>
            <p>To unlock more features for System13</p>
          </div>
          <form className="login-form" onSubmit={loginSubmitHandler}>
            <input
              type="email"
              value={enteredEmail}
              onChange={emailInputHandler}
              placeholder="email"
            />
            <input
              type="text"
              value={enteredPassword}
              placeholder="password"
              onChange={passwordInputHandler}
            />
            <NavLink to="/signup" className="u-remove-a-eff login-form__signup">
              Don't have an account?
            </NavLink>
            <motion.button type="submit" className="login-form__submit">
              &raquo;
            </motion.button>
          </form>
        </motion.div>
      </div>
    </React.Fragment>
  );
};

export default Profile;
