import React, { useRef, useCallback, useState } from "react";
import Modal from "../Layout/Modal";
import { motion } from "framer-motion";

const Players = (props) => {
  const codenameRef = useRef("");
  const nameRef = useRef("");
  const tierRef = useRef("");

  const submitHandler = (event) => {
    event.preventDefault();

    const player = {
      name: nameRef.current.value.trim(),
      tier: tierRef.current.value.trim(),
      codename: codenameRef.current.value.slice(0, 3).toUpperCase(),
    };

    console.log(player);
  };
  return (
    <React.Fragment>
      <motion.form
        onSubmit={submitHandler}
        className={`account__${
          props.inputType === "add" ? "add" : "remove"
        }Player`}
      >
        <input type="number" min="1" max="4" placeholder="tier" ref={tierRef} />
        <input type="text" placeholder="codename" ref={codenameRef} />
        <input type="text" placeholder="full name" ref={nameRef} />
        <button type="submit">{props.inputType === "add" ? "+" : "-"}</button>
      </motion.form>
    </React.Fragment>
  );
};

export default Players;
