import React, { useRef, useCallback, useState } from "react";
import Modal from "../Layout/Modal";
import { motion } from "framer-motion";

const Players = (props) => {
  const codenameRef = useRef("");
  const [codenameValid, setCodenameValid] = useState(true);
  const [isValid, setIsValid] = useState(true);

  const submitHandler = (event) => {
    event.preventDefault();
    if (codenameRef.current.value.length > 3) {
      console.warn(codenameRef.current.value);
      setIsValid(true);
      setIsValid(false);
      codenameRef.current.value = "";
    } else {
      setIsValid(true);
      // SEND TO BACKEND
    }
  };
  return (
    <React.Fragment>
      <motion.form
        onSubmit={submitHandler}
        className={`account__${
          props.inputType === "add" ? "add" : "remove"
        }Player ${isValid ? "" : "notValid"}`}
      >
        <input type="number" min="1" max="4" placeholder="tier" />
        <input type="text" placeholder="codename" ref={codenameRef} />
        <input type="text" placeholder="full name" />
        <button type="submit">{props.inputType === "add" ? "+" : "-"}</button>
      </motion.form>
    </React.Fragment>
  );
};

export default Players;
