import React, { useRef, useCallback, useState } from "react";
import Modal from "../Layout/Modal";

const Players = (props) => {
  const codenameRef = useRef("");

  const [modalVisible, setModalVisible] = useState(true);
  const [modalText, setModalText] = useState("default");
  const [modalError, setModalError] = useState(false);
  const liftingModalVisible = useCallback(() => {
    setModalVisible(false);
  }, []);

  const submitHandler = (event) => {
    event.preventDefault();
    if (codenameRef.current.value.length > 3) {
    }
  };
  return (
    <React.Fragment>
      <form
        onSubmit={submitHandler}
        className={`account__${
          props.inputType === "add" ? "add" : "remove"
        }Player`}
      >
        <input type="number" min="1" max="4" placeholder="tier" />
        <input type="text" placeholder="codename" ref={codenameRef} />
        <input type="text" placeholder="full name" />
        <button type="submit">{props.inputType === "add" ? "+" : "-"}</button>
      </form>
      <Modal
        liftingModalCancel={liftingModalVisible}
        isVisible={modalVisible}
        text={modalText}
        errorModal={modalError}
      />
    </React.Fragment>
  );
};

export default Players;
