import React, { Fragment } from "react";
import ReactDOM from "react-dom";
import { motion } from "framer-motion";

const SubModal = (props) => {
  <motion.section
  className={"modal warning"}
  initial={{ x: "-27vw", y: "-25vh" }}
  animate={props.isVisible ? {scale: 1 , y:"-25vh",x:"-27vw",opacity:1} : { y: "-25vh",x:"-100vw",opacity:0}}
  transition={{ease:"anticipate",duration:.6}}
>
  <h3 className="modal-header">{props.modalH}</h3>
  <p className="modal-info">{props.modalP}</p>
  <button
    className="btn-understand btn"
    onClick={props.liftingModalCancle}
  >
    {props.modalBtn}
  </button>
</motion.section>
}

const Modal = (props) => {
  return (
    <Fragment>


      <motion.section
        className={props.isVisible ? "overlay" : "overlay hidden"}
        animate={props.isVisible ? {x:0} : {x:"-100vw"}}
        transition={{type: "tween",duration:.5, delay:.3,}}
      ></motion.section>
    </Fragment>
  );
};

export default Modal;
